mapper.select:
SELECT *
FROM BOX_PERMISSION
WHERE member_id = #{member_id} and box_no = #{box_no}

mapper.update:
UPDATE BOX_PERMISSION


		SET
		MASTER_PER=#{master_per},
		READ_PER=0,
		WRITE_PER=0,
		EDIT_PER=0,
		DELETE_PER=0


		SET MASTER_PER=#{master_per}


WHERE MEMBER_ID = #{member_id}
and BOX_NO = #{box_no}

UPDATE BOX_PERMISSION
SET MEMBER_STAT= #{member_stat}, READ_PER=1, WRITE_PER=1, EDIT_PER=1, DELETE_PER=1
WHERE BOX_NO = #{box_no} AND MEMBER_ID = #{member_id}

mapper.select:
SELECT MEMBER_ID
FROM MEMBER
WHERE MEMBER_ID = #{member_id}
