mapper.select:


SELECT *
FROM member
WHERE 
    member_id=#{member_id}
    AND password=#{password}

mapper.update:
UPDATE member 
SET

rememberme = #{rememberme},
rememberage = #{rememberage}
WHERE member_id=#{member_id}

mapper.select:


SELECT member_id 
FROM member 
WHERE email=#{email}

mapper.update:
UPDATE member 
SET
password = #{password}
WHERE email = #{email}
