mapper.select:

SELECT
    /*+ index_desc(PRODUCT) */
    *
FROM PRODUCT
WHERE PRODUCT_NO = #{product_no}


SELECT
    /*+ index_desc(CATEGORY) */
    *
FROM CATEGORY
WHERE product_no = #{product_no}


SELECT
    /*+ index_desc(BASE_CATEGORY) */
    *
FROM BASE_CATEGORY
WHERE box_no = #{box_no}


SELECT count(product_no)
FROM product
WHERE box_no = #{box_no}

mapper.update:
UPDATE PRODUCT
SET BARCODE = #{barcode}
WHERE PRODUCT_NO = #{product_no}

mapper.select:
SELECT p.BOX_NO, b.MEMBER_ID, m.MEMBER_TYPE
FROM MEMBER m INNER JOIN BOX b ON b.MEMBER_ID = m.MEMBER_ID INNER JOIN PRODUCT p ON p.BOX_NO = b.BOX_NO
WHERE b.BOX_NO = #{box_no} and p.PRODUCT_NO = #{product_no}
