mapper.select:

SELECT
    /*+ index_desc(PRODUCT) */
    *
FROM PRODUCT
WHERE PRODUCT_NO = #{product_no}


SELECT
    /*+ index_desc(CATEGORY) */
    *
FROM CATEGORY
WHERE product_no = #{product_no}


SELECT
    /*+ index_desc(BASE_CATEGORY) */
    *
FROM BASE_CATEGORY
WHERE box_no = #{box_no}


SELECT count(product_no)
FROM product
WHERE box_no = #{box_no}
